var { weaponRule1, weaponRule2 } = params.effect;
var range = params.assignment.inRange?.value ?? 12;
var weapon = { attacks: params.effect.numberDigit.value, range, rules: [] };
if (weaponRule1) {
  weapon.rules.push(weaponRule1.name);
  this.applyRating(weapon, weaponRule1);
}
if (weaponRule2) {
  weapon.rules.push(weaponRule2.name);
  this.applyRating(weapon, weaponRule2);
}
var cost = this.getWeaponCost(weapon, { quality: 6 - (4 - params.threshold) })
var multiplier = params.assignment.target?.name === "model" ? this.SniperEffect : 1;
return cost * multiplier;

